import React from 'react'
import logo from './component/assets/img/RK logo.png'
import { useState } from "react";
import './component/css/index.css'
import { Link } from 'react-router-dom';

export default function Header() {
  const [sideBar, setSideBar] = useState(false);

  const NavigationLink = [
    {
      Title: "Home",
      Path: "/",
    },
    {
      Title: "About Us",
      Path: "/abouts"

    },
    {
      Title: "Sevices",
      Path: "/services"
    },
    {
      Title: "Our Client",
      Path: "/clientreview"
    },
    {
      Title: "Product",
      Path: "/product"
    },
    {
      Title: "Contact",
      Path: "/contact"
    }
  ];

  return (
    <>
      <aside
        id="logo-sidebar"
        className={
          sideBar
            ? "fixed top-0 left-[100%] right-0 z-40 w-64 h-screen pt-10 transition-transform -translate-x-full bg-white dark:bg-gray-800 dark:border-gray-700"
            : "hidden"
        }
        aria-label="Sidebar"
      >
        <div className="h-full px-3 pb-4 overflow-y-auto ">
          <ul className="space-y-2">
            <li>
              <div
                className="flex  py-2 pl-5 pr-4 text-base  md:border-0 md:p-0"
                onClick={() => setSideBar(false)}
              >
                <svg
                  className="w-6 h-6"
                  aria-hidden="true"
                  fill="currentColor"
                  viewBox="0 0 20 20"
                  xmlns="http://www.w3.org/2000/svg"
                >
                  <path
                    fillRule="evenodd"
                    d="M3 5a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM3 10a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1zM3 15a1 1 0 011-1h12a1 1 0 110 2H4a1 1 0 01-1-1z"
                    clipRule="evenodd"
                  ></path>
                </svg>
                <span className="block text-base md:border-0 md:p-0 pl-2">
                  close
                </span>
              </div>
            </li>
            {NavigationLink.map((item, index) => {
              return (
                <li key={index}>
                  <Link
                    to={item.Path}
                    className="block  py-2 pl-5 pr-4 text-base  md:border-0 md:p-0 hover-underline-animation"
                  >
                    {item.Title}
                  </Link>
                </li>
              );
            })}
          </ul>
        </div>
      </aside>
      {/* fixed-top */}
     
    </>
  )
}
